}
@font-face {
    font-family: 'gilroy-semibold';
    src: url('/fonts/Gilroy/Gilroy-SemiBold.ttf');
}
* {
    margin: 0;
    padding: 0;
}
html { background-color:#F6F6F6; }
.contentSection {
    margin-top: 70px;
    display: flex;
    justify-content: center;
    margin-bottom: 100px;
}
.content {
    display: flex;
    justify-content: space-between;
    width: 1210px;
}
.answerTitle {
    font-family: 'gilroy';
    font-size: 15px;
    line-height: 22.5px;
    margin-bottom: 27px;
    font-weight: 600;
    color: #2B1A51;
}
.answerList { 
    list-style: none;
    display: flex;
    flex-direction: column;
    list-style-image: url('../public/icons/listMarker.svg');
}
.answerList li span {
    display: flex;
    align-items: center;
}
/*Всплывающие подсказки*/
.tooltip {
    margin: 0;
    display: flex;
    position: relative;
    cursor: pointer;
    align-items: center;
}
.tooltipIcon {
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    height: 24px;
    width: 24px;
    border-radius: 45px;
    font-family: 'gilroy';
    font-size: 14px;
    color: #9f9f9f;
}
.tooltipIcon.dark { background-color: #ddd; }
.tooltipIcon.light { background-color: #fff; }
.promt {
    position: absolute;
    z-index: 1;
    top: 120%;
    left: -20em;
    right: -20em;
    width: -moz-max-content;
    width: -webkit-max-content;
    width: max-content;
    max-width: 25em; 
    max-height: 80vh;
    overflow: auto;
    visibility: hidden;
    margin: 0 auto .4em;
    padding: 36px;
    background: #fff;
    box-shadow: 8px 8px 44px 0px #00000014;
}
.promt, .promtText {
    font-family: 'gilroy-light';
    color: #757575;
    font-size: 90%;
    line-height: 140%;
}
.tooltip:before {
    content: "";
    position: absolute;
    top: 1.3em;
    left: 0;
    right: 0;
    height: 2em;
    visibility: hidden;
  }
.tooltip > .promt a {
    font-family: 'gilroy';
    color: #2B1A51;
}
.tooltip:hover > .promt, .tooltip:hover:before, .tooltip:hover:after, .tooltip:focus > .promt, .tooltip:focus:before, .tooltip:focus:after {
    visibility: visible;
    transition: 0s .2s;
}
.tooltip:focus { outline: none; }
.tooltipIcon:hover { color: #F46628;}
